using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

public sealed class SkyforgePlanner
{
    private enum MissionState
    {
        Planned,
        Completed,
        Aborted
    }

    // Compact mission model for in-memory planning state.
    private sealed class Mission
    {
        public int Id { get; }
        public string Codename { get; }
        public int PlannedMinutes { get; }
        public int? ActualMinutes { get; set; }
        public MissionState State { get; set; }

        public Mission(int id, string codename, int plannedMinutes)
        {
            Id = id;
            Codename = codename;
            PlannedMinutes = plannedMinutes;
            State = MissionState.Planned;
        }
    }

    private readonly Dictionary<int, Mission> _missions = new();
    private int _nextMissionId = 1;
    private int _score;

    public void AddMission(string codename, int plannedMinutes)
    {
        if (string.IsNullOrWhiteSpace(codename))
        {
            throw new ArgumentException(\"Mission codename is required.\", nameof(codename));
        }

        if (plannedMinutes <= 0)
        {
            throw new ArgumentOutOfRangeException(nameof(plannedMinutes), \"Planned minutes must be greater than zero.\");
        }

        var mission = new Mission(_nextMissionId++, codename.Trim(), plannedMinutes);
        _missions.Add(mission.Id, mission);
    }

    public void CompleteMission(int missionId, int actualMinutes)
    {
        if (actualMinutes <= 0)
        {
            throw new ArgumentOutOfRangeException(nameof(actualMinutes), \"Actual minutes must be greater than zero.\");
        }

        var mission = GetMission(missionId);
        EnsureMissionIsPlanned(mission);

        mission.ActualMinutes = actualMinutes;
        mission.State = MissionState.Completed;

        // Scoring rule: base gain is actual time, plus a bonus if target time is met/exceeded.
        _score += actualMinutes;
        if (actualMinutes >= mission.PlannedMinutes)
        {
            _score += 12;
        }
    }

    public void AbortMission(int missionId)
    {
        var mission = GetMission(missionId);
        EnsureMissionIsPlanned(mission);

        mission.State = MissionState.Aborted;
        _score -= 7;
    }

    public string GetStatusReport()
    {
        var builder = new StringBuilder();
        builder.AppendLine(\"Skyforge Planner Status\");
        builder.AppendLine("'$"Score: {_score}");

        foreach (var mission in _missions.Values.OrderBy(m => m.Id))
        {
            var actualLabel = mission.ActualMinutes?.ToString() ?? "-";
            builder.AppendLine(
                $"#{mission.Id} {mission.Codename} | planned={mission.PlannedMinutes}m | actual={actualLabel}m | state={ToStateToken(mission.State)}");
        }

        return builder.ToString().TrimEnd();
    }

    private Mission GetMission(int missionId)
    {
        if (!_missions.TryGetValue(missionId, out var mission))
        {
            throw new KeyNotFoundException($"Mission with id {missionId} was not found.");
        }

        return mission;
    }

    private static void EnsureMissionIsPlanned(Mission mission)
    {
        if (mission.State != MissionState.Planned)
        {
            throw new InvalidOperationException(
                $"Mission {mission.Id} cannot be changed because it is already {ToStateToken(mission.State)}.");
        }
    }

    private static string ToStateToken(MissionState state)
    {
        return state switch
        {
            MissionState.Planned => "planned",
            MissionState.Completed => "completed",
            MissionState.Aborted => "aborted",
            _ => throw new ArgumentOutOfRangeException(nameof(state), state, "Unknown mission state.")
        };
    }
}
